pragma solidity ^0.4.18;

import "./Ownable.sol";
import "./ERC20.sol";

/**
 * @title Airdrop
 * @dev Token Batch Transfer for Airdrop campaign
 */
contract Airdrop is Ownable {

  ERC20 token;

  /**
  * Contructor
  */
  constructor() public {
    // require(_token != address(0));

    token = ERC20(0xbC3e373D2B24FaF1DFe5b3F3ACff9d66a7b48869);
  }

  /* Batch token transfer. Used by contract creator to distribute initial tokens to holders */
  function batchTransfer(address[] _recipients, uint[] _values) external onlyOwner {
    require(_recipients.length > 0 && _recipients.length == _values.length);

    for (uint i = 0; i < _recipients.length; i++) {
      token.transfer(_recipients[i], _values[i]);
    }
  }

}
